import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the first thread's reply button
    await page.waitForSelector('.thread-action[data-action="reply"]');
    await page.click('.thread-action[data-action="reply"]');

    // Perform 10 open/close cycles
    for (let i = 0; i < 10; i++) {
      // Wait for and click the reply button
      await page.waitForSelector('.thread-action[data-action="reply"]', { state: 'visible' });
      await page.click('.thread-action[data-action="reply"]');

      // Wait for modal to open and click close button
      await page.waitForSelector('#close-reply-modal', { state: 'visible' });
      await page.click('#close-reply-modal');

      // Wait for modal to close
      await page.waitForSelector('#close-reply-modal', { state: 'hidden' });
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_completed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_completed.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times in succession",
      extractedData: {
        iterations: 10,
        lastAction: "Modal closed"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.threads-feed', { state: 'visible' });

    // Find all reply buttons using CSS selector
    const replyButtons = await page.$$('.thread-action[data-action="reply"]');

    if (replyButtons.length === 0) {
      throw new Error('No reply buttons found');
    }

    // Perform 10 rapid open/close cycles
    for (let i = 0; i < 10; i++) {
      // Click the first available reply button
      await replyButtons[0].click();

      // Wait for modal to appear with multiple selector strategies
      await Promise.race([
        page.waitForSelector('#close-reply-modal', { state: 'visible' }),
        page.waitForSelector('.modal-content:has-text("Reply")', { state: 'visible' })
      ]);

      // Take screenshot of opened modal on first iteration
      if (i === 0) {
        await page.screenshot({ path: path.join(screenshotsDir, `step_2_modal_open.png`) });
        screenshots.push(path.join(screenshotsDir, `step_2_modal_open.png`));
      }

      // Close the modal using multiple selector strategies
      const closeButton = await page.$('#close-reply-modal, .modal-close, [aria-label="Close"]');
      if (closeButton) {
        await closeButton.click();
      }

      // Wait for modal to disappear
      await Promise.race([
        page.waitForSelector('#close-reply-modal', { state: 'hidden' }),
        page.waitForSelector('.modal-content', { state: 'hidden' })
      ]);
    }

    // Take final screenshot after all operations
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times",
      extractedData: {
        cyclesCompleted: 10,
        modalOperations: "10 open/close cycles"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    const errorScreenshot = path.join(screenshotsDir, 'error.png');
    await page.screenshot({ path: errorScreenshot });

    return {
      success: false,
      action: "Failed to complete modal operations",
      extractedData: {},
      screenshots: [errorScreenshot],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and find a thread with reply button
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Find first thread with reply button
    const replyButton = await page.$('.thread-action[data-action="reply"]');
    if (!replyButton) {
      throw new Error('No reply button found');
    }

    // Perform 10 open/close cycles
    for (let i = 0; i < 10; i++) {
      // Click reply button
      await replyButton.click();

      // Wait for modal to open
      await page.waitForSelector('#reply-modal', { state: 'visible', timeout: 2000 });

      // Close modal
      const closeButton = await page.$('#close-reply-modal');
      if (closeButton) {
        await closeButton.click();
      }

      // Wait for modal to close
      await page.waitForSelector('#reply-modal', { state: 'hidden', timeout: 2000 });
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times",
      extractedData: { cyclesCompleted: 10 },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete 10 open/close cycles",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
