import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.threads-feed');

    // Find all visible like buttons
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click all like buttons in rapid succession
    for (const button of likeButtons) {
      await button.click();
      // Add minimal delay to ensure UI updates but still complete within 5 seconds
      await page.waitForTimeout(100);
    }

    // Take screenshot after clicking all like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_likes_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_likes_clicked.png'));

    // Extract data about the clicked buttons
    const extractedData = {
      totalLikesClicked: likeButtons.length,
      likeButtonsInfo: []
    };

    for (const button of likeButtons) {
      const threadId = await button.evaluate(el => el.closest('.thread-item')?.dataset.threadId);
      const isLiked = await button.evaluate(el => el.classList.contains('active'));
      extractedData.likeButtonsInfo.push({ threadId, isLiked });
    }

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

