import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Get all visible like buttons using multiple selector strategies
    const likeButtons = await page.$$eval('.thread-action[data-action="like"], [data-action="like"]', 
      buttons => buttons.filter(btn => btn.offsetParent !== null)
    );

    // Click all visible like buttons in rapid succession
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_likes_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_likes_clicked.png'));

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        likedCount: likeButtons.length,
        likeButtons: likeButtons.map(btn => ({
          text: btn.innerText,
          className: btn.className
        }))
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
