import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('#threads-feed', { timeout: 10000 });
    await page.waitForSelector('.thread-item', { timeout: 10000 });

    // Extract thread data
    const threadsData = await page.$$eval('.thread-item', (threads) => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent?.trim() || '';
        const time = thread.querySelector('.thread-time')?.textContent?.trim() || '';

        const actionSpans = thread.querySelectorAll('.thread-action span');
        const actions = Array.from(actionSpans).map(span => span.textContent?.trim()).filter(Boolean);

        return {
          text,
          time,
          actions
        };
      });
    });

    // Take screenshot of threads
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from current feed",
      extractedData: {
        threadCount: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

