import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('.threads-feed', { timeout: 10000 });

    // Wait for individual thread items to be present
    await page.waitForSelector('.thread-item', { timeout: 15000 });

    // Extract all thread content and metadata
    const threadsData = await page.$$eval('.thread-item', (threads) => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent?.trim() || '';
        const time = thread.querySelector('.thread-time')?.textContent?.trim() || '';

        // Extract action counts (likes, replies, reposts)
        const actionSpans = thread.querySelectorAll('.thread-action span');
        const actions = Array.from(actionSpans).map(span => span.textContent?.trim() || '');

        return {
          text,
          time,
          likes: actions[0] || '',
          replies: actions[1] || '',
          reposts: actions[2] || ''
        };
      });
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from current feed",
      extractedData: {
        threadsCount: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
