import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the thread textarea to be visible
    await page.waitForSelector('#thread-text');

    // Type the test text into the thread textarea
    const testText = 'Testing character count functionality';
    await page.fill('#thread-text', testText);

    // Wait for character count to update
    await page.waitForFunction(
      (expected) => {
        const countElement = document.querySelector('#char-count');
        return countElement && countElement.textContent.includes(expected);
      },
      { timeout: 5000 },
      testText.length.toString()
    );

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Extract character count information
    const charCountText = await page.textContent('#char-count');
    const extractedData = {
      typedText: testText,
      characterCount: charCountText,
      expectedLength: testText.length,
      actualLength: testText.length
    };

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete character count verification",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

