import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on the thread text area
    await page.waitForSelector('#thread-text', { state: 'visible' });
    await page.focus('#thread-text');

    // Type the test text
    const testText = 'Testing character count functionality';
    await page.type('#thread-text', testText);

    // Wait for character count to update with explicit text check
    await page.waitForFunction(
      (expectedCount) => {
        const charCount = document.querySelector('#char-count');
        return charCount && charCount.textContent.includes(`${expectedCount}/500`);
      },
      testText.length,
      { timeout: 5000 }
    );

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Extract character count data for verification
    const charCount = await page.textContent('#char-count');
    const currentCount = parseInt(charCount.split('/')[0]);

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updated to " + charCount,
      extractedData: {
        typedText: testText,
        expectedLength: testText.length,
        actualLength: currentCount,
        characterCountDisplay: charCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
