import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-menu');

    // Click on Search navigation item using multiple selector strategies
    const searchNavItem = await page.waitForSelector('.nav-item[data-view="search"]');
    await searchNavItem.click();

    // Wait for Search view to become active
    await page.waitForSelector('#search-view.active', { timeout: 5000 });

    // Verify the view has switched by checking URL and active state
    const isSearchActive = await page.$eval('#search-view', el => el.classList.contains('active'));
    const searchInputPlaceholder = await page.$eval('#search-input', el => el.placeholder);

    // Take screenshot after switching to Search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    return {
      success: true,
      action: "Successfully clicked on Search navigation item and switched to Search view",
      extractedData: {
        isSearchViewActive: isSearchActive,
        searchInputPlaceholder: searchInputPlaceholder,
        currentView: 'search'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to click on Search navigation item and switch to Search view",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-menu', { timeout: 5000 });

    // Click on Search navigation item using CSS selector
    const searchNavItem = await page.$(".nav-item[data-view='search']");
    if (!searchNavItem) {
      throw new Error('Search navigation item not found');
    }

    await searchNavItem.click();

    // Wait for search view to become active
    await page.waitForSelector('#search-view.active', { timeout: 3000 });

    // Verify search view is active
    const searchView = await page.$('#search-view.active');
    if (!searchView) {
      throw new Error('Search view did not become active after click');
    }

    // Take screenshot after successful navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Extract view information to verify success
    const activeView = await page.$eval('.view.active', el => el.id);
    const searchHeader = await page.$eval('#search-view .content-header', el => el.textContent?.trim());

    return {
      success: true,
      action: "Successfully clicked on Search navigation item and switched to Search view",
      extractedData: {
        activeView,
        searchHeader
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to click on Search navigation item",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

