import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to search view first
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');

    // Wait for search view to load
    await page.waitForSelector('#search-view.active');

    // Take screenshot after switching to search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Wait for and click on Hashtags tab
    await page.waitForSelector('.tab-btn[data-tab="hashtags"]');
    await page.click('.tab-btn[data-tab="hashtags"]');

    // Wait for tab activation
    await page.waitForSelector('.tab-btn[data-tab="hashtags"].active');

    // Take screenshot after switching to hashtags tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_tab.png'));

    // Extract active tab information
    const activeTab = await page.$eval('.tab-btn.active', el => el.textContent.trim());
    const activeTabData = await page.$eval('.tab-btn.active', el => ({
      text: el.textContent.trim(),
      dataTab: el.dataset.tab
    }));

    return {
      success: true,
      action: "Successfully switched to search view and clicked on Hashtags tab",
      extractedData: {
        activeTab: activeTab,
        tabData: activeTabData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item to switch to search view
    const searchNavItem = await page.waitForSelector('.nav-item[data-view="search"]');
    await searchNavItem.click();
    await page.waitForTimeout(1000); // Wait for view transition

    // Wait for search view to be active and take screenshot
    await page.waitForSelector('#search-view.active');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Click on Hashtags tab using multiple selector strategies
    // First try data-tab attribute selector
    const hashtagsTab = await page.waitForSelector('button.tab-btn[data-tab="hashtags"]');
    await hashtagsTab.click();
    await page.waitForTimeout(500); // Wait for tab switch animation

    // Verify the tab is now active
    const activeTab = await page.waitForSelector('button.tab-btn.active[data-tab="hashtags"]');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_tab.png'));

    // Extract tab state for verification
    const tabText = await activeTab.textContent();
    const isActive = await activeTab.evaluate(el => el.classList.contains('active'));

    return {
      success: true,
      action: "Successfully clicked on 'Hashtags' tab in search view",
      extractedData: {
        activeTab: tabText.trim(),
        isTabActive: isActive
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to click on 'Hashtags' tab in search view",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

