import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click emoji button
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_button_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_button_clicked.png'));

    // Wait for emoji picker and select a smile emoji
    await page.waitForSelector('.emoji-item');
    const smileEmojis = await page.$$('.emoji-item');
    if (smileEmojis.length > 0) {
      await smileEmojis[0].click(); // Select first available emoji
    }
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_selected.png'));

    // Verify emoji appears in thread text
    await page.waitForSelector('#thread-text');
    const threadText = await page.$eval('#thread-text', el => el.value);
    const containsEmoji = /\p{Emoji}/u.test(threadText);

    if (!containsEmoji) {
      throw new Error('Emoji was not added to thread text');
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_emoji_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_emoji_verified.png'));

    return {
      success: true,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
      extractedData: {
        threadText: threadText,
        containsEmoji: containsEmoji
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the emoji button
    await page.waitForSelector('#add-emoji', { timeout: 5000 });
    await page.click('#add-emoji');

    // Wait for emoji picker to appear and take screenshot
    await page.waitForSelector('.emoji-grid', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker.png'));

    // Find and click a smile emoji (using first available emoji)
    await page.waitForSelector('.emoji-item', { timeout: 5000 });
    const emojiText = await page.$eval('.emoji-item:first-child', el => el.textContent);
    await page.click('.emoji-item:first-child');

    // Wait for emoji to be added to textarea and verify
    await page.waitForFunction(
      (expectedEmoji) => {
        const textarea = document.querySelector('#thread-text');
        return textarea && textarea.value.includes(expectedEmoji);
      },
      { timeout: 5000 },
      emojiText
    );

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_added.png'));

    // Extract verification data
    const threadText = await page.$eval('#thread-text', el => el.value);

    return {
      success: true,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
      extractedData: {
        emojiUsed: emojiText,
        threadText: threadText,
        emojiFound: threadText.includes(emojiText)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the emoji button using CSS selector
    await page.waitForSelector('#add-emoji', { timeout: 5000 });
    await page.click('#add-emoji');

    // Wait for emoji picker to appear and take screenshot
    await page.waitForSelector('.emoji-item', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker.png'));

    // Find and click a smile emoji (using first available emoji)
    const emojiItems = await page.$$('.emoji-item');
    if (emojiItems.length > 0) {
      const emojiText = await emojiItems[0].textContent();
      await emojiItems[0].click();

      // Wait for emoji to be inserted into textarea
      await page.waitForFunction((emoji) => {
        const textarea = document.getElementById('thread-text');
        return textarea && textarea.value.includes(emoji);
      }, emojiText);

      // Verify emoji appears in thread text
      const textareaValue = await page.$eval('#thread-text', el => el.value);
      const containsEmoji = textareaValue.includes(emojiText);

      if (!containsEmoji) {
        throw new Error('Emoji was not inserted into the thread text');
      }

      // Take final verification screenshot
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_verified.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_emoji_verified.png'));

      return {
        success: true,
        action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
        extractedData: {
          selectedEmoji: emojiText,
          threadText: textareaValue
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('No emoji items found in the picker');
    }
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {
      console.log('Could not take error screenshot:', e.message);
    }

    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
