import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_clicked.png'));

    // Wait for and click Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme change
    const themeValue = await page.$eval('#theme-select', select => select.value);
    const bodyClass = await page.$eval('body', body => body.className);

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        selectedTheme: themeValue,
        bodyClass: bodyClass
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

