import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for suggested accounts to load
    await page.waitForSelector('.account-item', { timeout: 5000 });

    // Extract data from first suggested account
    const accountData = await page.$eval('.account-item:first-child', el => ({
      username: el.querySelector('.username')?.textContent || '',
      handle: el.querySelector('.user-handle')?.textContent || '',
      buttonText: el.querySelector('.follow-btn')?.textContent || ''
    }));

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the follow button on first suggested account
    await page.click('.account-item:first-child .follow-btn');

    // Wait for state change (button text might update)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Get updated button state
    const updatedButtonText = await page.$eval('.account-item:first-child .follow-btn', el => el.textContent);

    return {
      success: true,
      action: "Clicked Follow button on first suggested account",
      extractedData: {
        initialAccount: accountData,
        updatedButtonState: updatedButtonText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

