import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for right sidebar to load and be visible
    await page.waitForSelector('.right-sidebar', { state: 'visible', timeout: 10000 });

    // Wait for suggested accounts section
    await page.waitForSelector('.suggested-accounts', { state: 'visible', timeout: 10000 });

    // Wait for the first account item to be present
    await page.waitForSelector('.account-item:first-child', { timeout: 10000 });

    // Wait for the follow button in the first account item
    const followButton = await page.waitForSelector('.account-item:first-child .follow-btn', { 
      state: 'visible', 
      timeout: 10000 
    });

    // Take screenshot before clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_click.png'));

    // Click the follow button
    await followButton.click();

    // Wait for state change (button might change text or style)
    await page.waitForTimeout(1000);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_click.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_click.png'));

    // Extract account information to verify action
    const accountInfo = await page.evaluate(() => {
      const firstAccount = document.querySelector('.account-item:first-child');
      return {
        username: firstAccount.querySelector('.username')?.textContent,
        handle: firstAccount.querySelector('.user-handle')?.textContent,
        buttonText: firstAccount.querySelector('.follow-btn')?.textContent
      };
    });

    return {
      success: true,
      action: "Clicked Follow button on first suggested account in right sidebar",
      extractedData: accountInfo,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
