import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.thread-item', { timeout: 10000 });

    // Extract thread data
    const threads = await page.$$eval('.thread-item', (threadElements) => {
      return threadElements.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent || '';
        const time = thread.querySelector('.thread-time')?.textContent || '';
        const actionSpans = Array.from(thread.querySelectorAll('.thread-action span'));
        const actions = actionSpans.map(span => span.textContent).filter(Boolean);

        return {
          text: text.trim(),
          time: time.trim(),
          actions: actions
        };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: { threads },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

