import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on the thread text area
    await page.waitForSelector('#thread-text');
    const textArea = await page.$('#thread-text');
    await textArea.focus();

    // Take screenshot before typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_before_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_before_typing.png'));

    // Type the test text
    const testText = 'Testing character count functionality';
    await textArea.type(testText);

    // Wait for character count to update
    await page.waitForFunction(
      (expectedCount) => {
        const countElement = document.querySelector('#char-count');
        return countElement && countElement.textContent.includes(expectedCount);
      },
      { timeout: 5000 },
      testText.length.toString()
    );

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_after_typing.png'));

    // Extract character count data for verification
    const charCountText = await page.$eval('#char-count', el => el.textContent);
    const currentCount = parseInt(charCountText.split('/')[0]);

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates",
      extractedData: {
        initialCount: 0,
        finalCount: currentCount,
        expectedCount: testText.length,
        characterCountText: charCountText
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

