import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on the thread text area
    await page.waitForSelector('textarea#thread-text');
    const textArea = await page.$('textarea#thread-text');
    await textArea.click();

    // Type the test text
    const testText = 'Testing character count functionality';
    await page.keyboard.type(testText, { delay: 50 });

    // Wait for character count to update
    await page.waitForFunction(
      (expectedCount) => {
        const countElement = document.querySelector('#char-count');
        return countElement && countElement.textContent.includes(expectedCount);
      },
      { timeout: 5000 },
      testText.length.toString()
    );

    // Verify character count update
    const charCount = await page.$eval('#char-count', el => el.textContent);
    const expectedCount = `${testText.length}/500`;

    if (charCount !== expectedCount) {
      throw new Error(`Character count mismatch. Expected: ${expectedCount}, Got: ${charCount}`);
    }

    // Take screenshot after verification
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_character_count_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_character_count_verified.png'));

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates correctly",
      extractedData: {
        initialCount: "0/500",
        finalCount: charCount,
        expectedCount: expectedCount,
        textLength: testText.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'step_error.png') });
      screenshots.push(path.join(screenshotsDir, 'step_error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete character count verification",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
