import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');
    await page.waitForTimeout(1000); // Additional wait for dynamic content

    // Find all visible like buttons
    const likeButtons = await page.$$(".thread-action[data-action='like']:visible");

    // Click all like buttons in rapid succession
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    }

    // Take screenshot after clicking likes
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    // Verify completion by counting clicked buttons
    const clickedCount = likeButtons.length;
    const likeStates = await page.$$eval(".thread-action[data-action='like']", buttons => 
      buttons.map(btn => ({
        isLiked: btn.classList.contains('active'),
        text: btn.querySelector('.action-count')?.textContent || '0'
      }))
    );

    return {
      success: true,
      action: `Clicked like buttons on ${clickedCount} visible threads`,
      extractedData: {
        totalThreads: clickedCount,
        likeStates: likeStates
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

