import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Find all visible like buttons using multiple selector strategies
    const likeButtons = await page.$$eval('.thread-action[data-action="like"], [data-action="like"].thread-action, button.thread-action[data-action="like"]', 
      buttons => buttons.filter(btn => {
        const rect = btn.getBoundingClientRect();
        return rect.width > 0 && rect.height > 0;
      })
    );

    // Click all visible like buttons in rapid succession
    const clickPromises = likeButtons.map(async (button, index) => {
      await button.scrollIntoViewIfNeeded();
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    });

    // Execute all clicks within 5 seconds
    await Promise.race([
      Promise.all(clickPromises),
      new Promise(resolve => setTimeout(resolve, 5000))
    ]);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    return {
      success: true,
      action: "Clicked like buttons on all visible threads within 5 seconds",
      extractedData: {
        likeButtonsClicked: likeButtons.length,
        timestamp: new Date().toISOString()
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
