export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Navigate to search view
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');

    // Wait for search view to load
    await page.waitForSelector('#search-view.active');

    // Take screenshot after navigating to search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Click on Hashtags tab
    await page.waitForSelector('.tab-btn[data-tab="hashtags"]');
    await page.click('.tab-btn[data-tab="hashtags"]');

    // Wait for tab to become active
    await page.waitForSelector('.tab-btn[data-tab="hashtags"].active');

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_tab.png'));

    // Verify tab is active
    const isActive = await page.$eval('.tab-btn[data-tab="hashtags"]', el => el.classList.contains('active'));

    return {
      success: true,
      action: "Successfully clicked on Hashtags tab in search view",
      extractedData: {
        hashtagsTabActive: isActive
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item to open search view
    const searchNavItem = await page.waitForSelector('.nav-item[data-view="search"]');
    await searchNavItem.click();

    // Wait for search view to become active
    await page.waitForSelector('#search-view.active', { timeout: 5000 });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Click on Hashtags tab using multiple selector strategies
    const hashtagsTab = await page.waitForSelector('button.tab-btn[data-tab="hashtags"]', { timeout: 3000 });
    await hashtagsTab.click();

    // Wait for tab to become active and verify
    await page.waitForFunction(() => {
      const tab = document.querySelector('button.tab-btn[data-tab="hashtags"]');
      return tab && tab.classList.contains('active');
    }, { timeout: 3000 });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_active.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_active.png'));

    // Extract verification data
    const extractedData = {
      activeTab: await page.$eval('button.tab-btn.active', el => el.textContent.trim()),
      tabDataAttribute: await page.$eval('button.tab-btn.active', el => el.getAttribute('data-tab'))
    };

    return {
      success: true,
      action: "Successfully switched to Hashtags tab in search view",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Capture error screenshot
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (screenshotError) {
      console.error('Failed to capture error screenshot:', screenshotError);
    }

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

