import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Take screenshot after threads load
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_loaded.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_loaded.png'));

    // Click the reply button on the first thread
    const replyButton = await page.waitForSelector('.thread-item:first-child .thread-action[data-action="reply"]');
    await replyButton.click();

    // Wait for reply modal to appear
    await page.waitForSelector('#reply-image-input', { timeout: 3000 });

    // Take screenshot after opening reply modal
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_reply_modal_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_reply_modal_open.png'));

    // Extract data to verify task completion
    const modalVisible = await page.isVisible('.modal-content');
    const replyInputVisible = await page.isVisible('#reply-image-input');

    return {
      success: true,
      action: "Clicked reply button on first thread and opened reply modal",
      extractedData: {
        modalOpened: modalVisible,
        replyInputAvailable: replyInputVisible
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

