import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click emoji button using CSS selector
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_button_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_button_clicked.png'));

    // Wait for emoji picker to appear and select first smile emoji
    await page.waitForSelector('.emoji-item');
    const emojiItems = await page.$$('.emoji-item');
    const firstEmoji = await emojiItems[0].textContent();
    await emojiItems[0].click();
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_selected.png'));

    // Verify emoji appears in thread text using XPath selector
    const textarea = await page.$('#thread-text');
    const textContent = await textarea.evaluate(el => el.value);

    if (!textContent.includes(firstEmoji)) {
      throw new Error(`Emoji ${firstEmoji} not found in thread text`);
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_emoji_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_emoji_verified.png'));

    return {
      success: true,
      action: "Clicked emoji button, selected smile emoji, and verified it appears in thread text",
      extractedData: {
        selectedEmoji: firstEmoji,
        threadTextContent: textContent
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
