import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');
    await page.waitForTimeout(1000); // Wait for view transition
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Wait for search tabs to be visible and click on Hashtags tab
    await page.waitForSelector('.tab-btn[data-tab="hashtags"]');
    await page.click('.tab-btn[data-tab="hashtags"]');
    await page.waitForTimeout(500); // Wait for tab activation

    // Verify the Hashtags tab is now active
    const isHashtagsActive = await page.$eval('.tab-btn[data-tab="hashtags"]', el => 
      el.classList.contains('active')
    );

    // Take screenshot after clicking Hashtags tab
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_active.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_active.png'));

    // Extract current active tab information
    const activeTab = await page.$eval('.tab-btn.active', el => ({
      text: el.textContent.trim(),
      dataTab: el.getAttribute('data-tab')
    }));

    return {
      success: true,
      action: "Successfully clicked on 'Hashtags' tab in search view",
      extractedData: {
        activeTab: activeTab.text,
        tabDataAttribute: activeTab.dataTab,
        isHashtagsActive: isHashtagsActive
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

