import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item to open search view
    const searchNavItem = page.locator('[data-view="search"]');
    await searchNavItem.waitFor({ state: 'visible' });
    await searchNavItem.click();

    await page.waitForTimeout(1000); // Wait for view transition
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Wait for search tabs to be visible and click on Hashtags tab
    const hashtagsTab = page.locator('button.tab-btn[data-tab="hashtags"]');
    await hashtagsTab.waitFor({ state: 'visible' });

    // Verify current active tab before clicking
    const initialActiveTab = await page.locator('.tab-btn.active').textContent();

    // Click on Hashtags tab
    await hashtagsTab.click();
    await page.waitForTimeout(500); // Wait for tab switch animation

    // Verify tab switch was successful
    await page.waitForFunction(() => {
      const activeTab = document.querySelector('.tab-btn.active');
      return activeTab && activeTab.textContent.includes('Hashtags');
    });

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_hashtags_active.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_hashtags_active.png'));

    // Extract verification data
    const activeTabText = await page.locator('.tab-btn.active').textContent();
    const hashtagResults = await page.$$eval('.hashtag-result', elements => elements.length);

    return {
      success: true,
      action: "Switched to Hashtags tab in search view",
      extractedData: {
        previousActiveTab: initialActiveTab,
        currentActiveTab: activeTabText,
        hashtagResultsCount: hashtagResults
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
