import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for the sidebar to load and be visible
    await page.waitForSelector('.sidebar', { state: 'visible' });

    // Multiple selector strategies for the Settings button
    // Strategy 1: Using ID selector (most reliable)
    const settingsButton = await page.$('#settings-btn');

    // Strategy 2: Using text content if ID fails
    if (!settingsButton) {
      await page.waitForSelector('text=Settings', { timeout: 2000 });
    }

    // Strategy 3: Using XPath as fallback
    if (!settingsButton) {
      await page.waitForSelector('//span[text()="Settings"]', { timeout: 2000 });
    }

    // Click the Settings button using the most reliable method
    await page.click('#settings-btn');

    // Wait for settings modal to appear - check for multiple possible indicators
    await Promise.race([
      page.waitForSelector('#settings-username', { timeout: 5000 }),
      page.waitForSelector('.modal-content', { timeout: 5000 }),
      page.waitForSelector('text=Account Settings', { timeout: 5000 })
    ]);

    // Take screenshot after opening settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_open.png'));

    // Verify settings modal is open by checking for settings elements
    const isSettingsVisible = await page.$eval('#settings-username', el => el.offsetParent !== null);

    return {
      success: true,
      action: "Clicked Settings button in left sidebar and opened settings modal",
      extractedData: {
        settingsModalVisible: isSettingsVisible,
        settingsElementsFound: await page.$$eval('.setting-item', items => items.length)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the Settings button using multiple selector strategies
    const settingsSelectors = [
      '#settings-btn', // Primary ID selector
      'li.nav-item:has-text("Settings")', // Text-based selector
      'xpath=//li[contains(@class, "nav-item") and .//span[text()="Settings"]]' // XPath selector
    ];

    let settingsButton = null;
    for (const selector of settingsSelectors) {
      try {
        settingsButton = await page.waitForSelector(selector, { timeout: 5000 });
        if (settingsButton) break;
      } catch (e) {
        continue;
      }
    }

    if (!settingsButton) {
      throw new Error('Settings button not found with any selector');
    }

    await settingsButton.click();

    // Wait for settings modal to appear using multiple indicators
    await Promise.race([
      page.waitForSelector('#settings-username', { timeout: 5000 }),
      page.waitForSelector('#settings-display-name', { timeout: 5000 }),
      page.waitForSelector('.modal-content:has-text("Settings")', { timeout: 5000 })
    ]);

    // Take screenshot after opening settings
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_modal.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_modal.png'));

    // Extract data from settings modal to verify it opened
    const usernameValue = await page.$eval('#settings-username', input => input.value);
    const displayNameValue = await page.$eval('#settings-display-name', input => input.value);

    return {
      success: true,
      action: "Clicked Settings button and opened settings modal",
      extractedData: {
        settings_username: usernameValue,
        settings_display_name: displayNameValue
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to click Settings button and open modal",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
