import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and find the first thread with reply button
    await page.waitForSelector('.threads-feed');

    // Perform 10 iterations of opening and closing reply modal
    for (let i = 0; i < 10; i++) {
      // Find and click reply button
      await page.waitForSelector('.thread-action[data-action="reply"]');
      await page.click('.thread-action[data-action="reply"]');

      // Wait for modal to appear and take screenshot on first iteration
      await page.waitForSelector('#close-reply-modal', { state: 'visible' });
      if (i === 0) {
        await page.screenshot({ path: path.join(screenshotsDir, 'step_2_modal_open.png') });
        screenshots.push(path.join(screenshotsDir, 'step_2_modal_open.png'));
      }

      // Close the modal
      await page.click('#close-reply-modal');

      // Wait for modal to close
      await page.waitForSelector('#close-reply-modal', { state: 'hidden' });

      // Add a small delay between iterations
      await page.waitForTimeout(100);
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times in succession",
      extractedData: {
        iterations: 10,
        modalOpenTime: "rapid succession"
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to open and close reply modal 10 times",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

