import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and find the first thread with reply button
    await page.waitForSelector('.thread-action[data-action="reply"]', { timeout: 5000 });

    const cyclesCompleted = [];

    // Perform 10 open/close cycles
    for (let i = 0; i < 10; i++) {
      // Click reply button using CSS selector
      await page.click('.thread-action[data-action="reply"]');

      // Wait for modal to open using different strategies
      await Promise.race([
        page.waitForSelector('#close-reply-modal', { state: 'visible', timeout: 1000 }),
        page.waitForSelector('.modal-content', { state: 'visible', timeout: 1000 })
      ]);

      // Click close button using CSS selector
      await page.click('#close-reply-modal');

      // Wait for modal to close
      await Promise.race([
        page.waitForSelector('#close-reply-modal', { state: 'hidden', timeout: 1000 }),
        page.waitForSelector('.modal-content', { state: 'hidden', timeout: 1000 })
      ]);

      cyclesCompleted.push(`Cycle ${i+1} completed`);

      // Small delay between cycles
      await page.waitForTimeout(100);
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_final.png'));

    return {
      success: true,
      action: "Successfully opened and closed reply modal 10 times",
      extractedData: {
        cycles: cyclesCompleted.length,
        cycleDetails: cyclesCompleted
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
