import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click emoji button
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');

    // Wait for emoji picker to appear and take screenshot
    await page.waitForSelector('.emoji-item');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_open.png'));

    // Find and click a smile emoji (using first available emoji)
    const emojiItems = await page.$$('.emoji-item');
    if (emojiItems.length > 0) {
      const emojiText = await emojiItems[0].textContent();
      await emojiItems[0].click();

      // Verify emoji appears in thread text
      await page.waitForFunction((emoji) => {
        const textarea = document.querySelector('#thread-text');
        return textarea && textarea.value.includes(emoji);
      }, emojiText);

      // Take final verification screenshot
      await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_added.png') });
      screenshots.push(path.join(screenshotsDir, 'step_3_emoji_added.png'));

      // Extract verification data
      const threadText = await page.$eval('#thread-text', el => el.value);

      return {
        success: true,
        action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
        extractedData: {
          emojiUsed: emojiText,
          threadText: threadText,
          emojiFound: threadText.includes(emojiText)
        },
        screenshots,
        error: null
      };
    } else {
      throw new Error('No emoji items found in picker');
    }
  } catch (error) {
    // Take error screenshot if possible
    try {
      await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
      screenshots.push(path.join(screenshotsDir, 'error.png'));
    } catch (e) {}

    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

