import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click the emoji button using CSS selector
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');
    await page.waitForTimeout(1000); // Wait for emoji picker to animate

    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_picker_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_picker_opened.png'));

    // Wait for emoji items to be visible and select a smile emoji
    await page.waitForSelector('.emoji-item');
    const emojiItems = await page.$$('.emoji-item');

    // Find a smile emoji (typically first few items)
    if (emojiItems.length > 0) {
      await emojiItems[0].click(); // Click first emoji (assuming it's a smile)
    }

    await page.waitForTimeout(500); // Wait for emoji to be inserted

    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_selected.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_selected.png'));

    // Verify emoji appears in thread text
    const threadText = await page.$eval('#thread-text', el => el.value);
    const containsEmoji = /[\u{1F600}-\u{1F64F}]/u.test(threadText);

    if (!containsEmoji) {
      throw new Error('Emoji was not successfully added to the thread text');
    }

    return {
      success: true,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
      extractedData: {
        threadText: threadText,
        emojiDetected: containsEmoji
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete emoji selection task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
