import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and ensure we're on home view
    await page.waitForSelector('#home-view.active', { timeout: 10000 });
    await page.waitForSelector('.threads-feed', { timeout: 10000 });

    // Wait for thread items to be present
    await page.waitForSelector('.thread-item', { timeout: 15000 });

    // Extract all thread data
    const threadsData = await page.$$eval('.thread-item', threads => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent || '';
        const time = thread.querySelector('.thread-time')?.textContent || '';
        const actions = Array.from(thread.querySelectorAll('.thread-action span'))
          .map(span => span.textContent)
          .filter(text => text.trim() !== '');

        return {
          text: text.trim(),
          time: time.trim(),
          actions: actions
        };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from current feed",
      extractedData: {
        threadsCount: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed', { timeout: 10000 });

    // Extract all thread data
    const threadsData = await page.$$eval('#threads-feed .thread-item', (threads) => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent?.trim() || '';
        const time = thread.querySelector('.thread-time')?.textContent?.trim() || '';

        // Extract action counts
        const actionSpans = Array.from(thread.querySelectorAll('.thread-action span'));
        const actions = {};

        actionSpans.forEach((span, index) => {
          if (index % 2 === 1) { // Get count values (odd indices)
            const actionType = actionSpans[index-1]?.textContent?.trim() || `action_${index}`;
            actions[actionType] = span.textContent.trim();
          }
        });

        return {
          text,
          time,
          actions
        };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the feed",
      extractedData: {
        threadCount: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract thread content and metadata",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

