import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');
    await page.waitForSelector('.thread-text', { timeout: 5000 });

    // Extract all thread data
    const threadsData = await page.$$eval('#threads-feed > *', threads => {
      return threads.map(thread => {
        const textEl = thread.querySelector('.thread-text');
        const timeEl = thread.querySelector('.thread-time');
        const actionSpans = thread.querySelectorAll('.thread-action span');

        return {
          text: textEl ? textEl.textContent.trim() : '',
          time: timeEl ? timeEl.textContent.trim() : '',
          actions: Array.from(actionSpans).map(span => span.textContent.trim())
        };
      });
    });

    // Take screenshot of threads
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: {
        threads: threadsData,
        count: threadsData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

