import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Wait for thread items to be present
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Extract all thread content and metadata
    const threadsData = await page.$$eval('.thread-item', (threadElements) => {
      return threadElements.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent?.trim() || '';
        const time = thread.querySelector('.thread-time')?.textContent?.trim() || '';
        const actions = Array.from(thread.querySelectorAll('.thread-action span'))
          .map(span => span.textContent?.trim())
          .filter(Boolean);

        return { text, time, actions };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: {
        threadsCount: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to extract thread content and metadata",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
