import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on search navigation item
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');
    await page.waitForSelector('#search-view.active');

    // Wait for search input to be visible and type 'jun'
    await page.waitForSelector('#search-input');
    await page.fill('#search-input', 'jun');

    // Take screenshot after typing in search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_typed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_typed.png'));

    // Switch to People tab
    await page.waitForSelector('.tab-btn[data-tab="people"]');
    await page.click('.tab-btn[data-tab="people"]');

    // Wait for tab switch to complete
    await page.waitForTimeout(500);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_people_tab.png'));

    // Extract verification data
    const searchValue = await page.$eval('#search-input', el => el.value);
    const activeTab = await page.$eval('.tab-btn.active', el => el.textContent.trim());

    return {
      success: true,
      action: "Navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData: {
        searchValue,
        activeTab
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

