import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click on Search navigation item to switch to search view
    const searchNavItem = await page.waitForSelector('.nav-item[data-view="search"]');
    await searchNavItem.click();

    // Wait for search view to become active
    await page.waitForSelector('#search-view.active', { timeout: 5000 });

    // Take screenshot after switching to search view
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Type 'jun' in the search input
    const searchInput = await page.waitForSelector('#search-input');
    await searchInput.fill('jun');

    // Take screenshot after typing in search
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_typed_jun.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_typed_jun.png'));

    // Switch to People tab
    const peopleTab = await page.waitForSelector('.tab-btn[data-tab="people"]');
    await peopleTab.click();

    // Wait for People tab to become active
    await page.waitForTimeout(1000); // Allow UI to update

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_people_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_people_tab.png'));

    // Extract search results data
    const extractedData = {
      searchQuery: 'jun',
      activeTab: 'people',
      resultsCount: await page.$$eval('.user-item', items => items.length)
    };

    return {
      success: true,
      action: "Successfully navigated to search view, typed 'jun' in search input, and switched to People tab",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
