import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and ensure we're on home view
    await page.waitForSelector('#home-view.active', { timeout: 5000 });

    // Wait for threads to be populated
    await page.waitForSelector('.thread-text', { timeout: 10000 });

    // Extract thread data
    const threadData = await page.evaluate(() => {
      const threads = [];
      const threadElements = document.querySelectorAll('.thread-item');

      threadElements.forEach(thread => {
        const text = thread.querySelector('.thread-text')?.textContent || '';
        const time = thread.querySelector('.thread-time')?.textContent || '';
        const actionSpans = Array.from(thread.querySelectorAll('.thread-action span'))
          .map(span => span.textContent)
          .filter(text => text.trim() !== '');

        threads.push({
          text: text.trim(),
          time: time.trim(),
          actions: actionSpans
        });
      });

      return threads;
    });

    // Take screenshot after data extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_data_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_data_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: {
        threads: threadData,
        threadCount: threadData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract thread data",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
