import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on thread textarea
    await page.waitForSelector('#thread-text');
    await page.focus('#thread-text');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_focused_textarea.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_focused_textarea.png'));

    // Type the test text
    const testText = 'Testing character count functionality';
    await page.type('#thread-text', testText);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_typed_text.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_typed_text.png'));

    // Verify character count updates
    await page.waitForSelector('#char-count');
    const charCountText = await page.textContent('#char-count');
    const expectedCount = `${testText.length}/500`;

    if (charCountText !== expectedCount) {
      throw new Error(`Character count mismatch. Expected: ${expectedCount}, Got: ${charCountText}`);
    }

    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_verified_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_verified_count.png'));

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates to 38/500",
      extractedData: {
        textEntered: testText,
        characterCount: charCountText,
        expectedCount: expectedCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and focus on the thread text area
    await page.waitForSelector('#thread-text');
    await page.focus('#thread-text');

    // Type the test text
    const testText = 'Testing character count functionality';
    await page.keyboard.type(testText, { delay: 50 });

    // Wait for character count to update
    await page.waitForFunction(
      (expectedLength) => {
        const countElement = document.querySelector('#char-count');
        return countElement && countElement.textContent.includes(expectedLength);
      },
      38, // Expected length of the test text
      { timeout: 5000 }
    );

    // Verify character count update
    const charCount = await page.$eval('#char-count', el => el.textContent);
    const finalText = await page.$eval('#thread-text', el => el.value);

    // Take verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_character_count.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_character_count.png'));

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates",
      extractedData: {
        typedText: finalText,
        characterCount: charCount,
        expectedLength: testText.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for textarea to be visible and focused
    await page.waitForSelector('#thread-text');
    const textArea = await page.$('#thread-text');
    await textArea.click();
    await page.waitForTimeout(500);

    // Type the test text
    const testText = 'Testing character count functionality';
    await textArea.type(testText, { delay: 50 });

    // Take screenshot after typing
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_typing.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_typing.png'));

    // Wait for character count update
    await page.waitForFunction(
      (expectedCount) => {
        const charCount = document.querySelector('#char-count');
        return charCount && charCount.textContent.includes(expectedCount);
      },
      { timeout: 5000 },
      testText.length.toString()
    );

    // Verify character count
    const charCountText = await page.textContent('#char-count');
    const extractedData = { characterCount: charCountText };

    // Take final verification screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_verified.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_verified.png'));

    return {
      success: true,
      action: "Typed 'Testing character count functionality' and verified character count updates to 36/500",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
