import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-menu', { timeout: 5000 });

    // Click on Profile navigation item using CSS selector
    const profileNavItem = await page.waitForSelector('.nav-item[data-view="profile"]', { timeout: 5000 });
    await profileNavItem.click();

    // Wait for profile view to load
    await page.waitForSelector('#profile-view.active', { timeout: 5000 });

    // Take screenshot after navigation
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_view.png'));

    // Extract user profile data
    const profileData = await page.evaluate(() => {
      const username = document.querySelector('.profile-username')?.textContent;
      const handle = document.querySelector('.profile-handle')?.textContent;
      const bio = document.querySelector('.profile-bio')?.textContent;
      return { username, handle, bio };
    });

    return {
      success: true,
      action: "Clicked on Profile navigation item and viewed current user's profile",
      extractedData: profileData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

