import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for navigation menu to load
    await page.waitForSelector('.nav-menu', { state: 'visible' });

    // Click on Profile navigation item using multiple selector strategies
    const profileSelector = '.nav-item[data-view="profile"]';
    await page.waitForSelector(profileSelector);
    await page.click(profileSelector);

    // Wait for profile view to load and become active
    await page.waitForSelector('#profile-view.active', { state: 'visible' });

    // Take screenshot after clicking profile
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_profile_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_profile_view.png'));

    // Extract user profile information to verify task completion
    const extractedData = {
      profileViewActive: await page.$eval('#profile-view', el => el.classList.contains('active')),
      username: await page.$eval('.profile-username', el => el.textContent?.trim()),
      userHandle: await page.$eval('.profile-handle', el => el.textContent?.trim())
    };

    return {
      success: true,
      action: "Clicked on Profile navigation item to view current user's profile",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
