      success: verificationResult,
      action: "Clicked emoji button, selected a smile emoji, and verified it appears in thread text",
      extractedData: {
        selectedEmoji: emojiText,
        threadContent: threadText,
        emojiFound: verificationResult
      },
      screenshots,
      error: verificationResult ? null : "Emoji not found in thread text"
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
