import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for and click emoji button using CSS selector
    await page.waitForSelector('#add-emoji');
    await page.click('#add-emoji');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_emoji_button_clicked.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_emoji_button_clicked.png'));

    // Wait for emoji picker to appear and select first smile emoji
    await page.waitForSelector('.emoji-item');
    const emojiItems = await page.$$('.emoji-item');

    // Find a smile emoji (look for common smile emojis)
    let selectedEmoji = null;
    for (const emoji of emojiItems) {
      const emojiText = await emoji.textContent();
      if (['😊', '😀', '😂', '🤣', '😍', '🥰'].includes(emojiText)) {
        selectedEmoji = emojiText;
        await emoji.click();
        break;
      }
    }

    // If no specific smile emoji found, click first available emoji
    if (!selectedEmoji && emojiItems.length > 0) {
      selectedEmoji = await emojiItems[0].textContent();
      await emojiItems[0].click();
    }

    // Verify emoji appears in thread text
    await page.waitForFunction((emoji) => {
      const textarea = document.querySelector('#thread-text');
      return textarea && textarea.value.includes(emoji);
    }, selectedEmoji);

    // Take screenshot of result
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_emoji_added.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_emoji_added.png'));

    // Extract verification data
    const threadText = await page.$eval('#thread-text', el => el.value);

    return {
      success: true,
      action: "Clicked emoji button, selected smile emoji, and verified it appears in thread text",
      extractedData: {
        selectedEmoji: selectedEmoji,
        threadText: threadText,
        emojiPresent: threadText.includes(selectedEmoji)
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
