import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for thread feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Wait for thread items to be present
    await page.waitForSelector('.thread-item', { timeout: 3000 }).catch(() => {
      console.log('No thread items found - returning empty array');
    });

    // Extract all visible usernames from thread feed
    const usernames = await page.$$eval('.thread-username', 
      elements => elements.map(el => el.textContent.trim())
    );

    // Take screenshot of thread feed with usernames
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_usernames_visible.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_usernames_visible.png'));

    return {
      success: true,
      action: "Collected all visible usernames from thread feed",
      extractedData: { usernames },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

