import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and ensure we're on the home view
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Find all visible like buttons within threads
    const likeButtons = await page.$$('.thread-action[data-action="like"]:visible');

    // Click all like buttons in rapid succession
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    }

    // Take screenshot after clicking all like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_liking.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_liking.png'));

    // Extract data to verify completion
    const likedCount = likeButtons.length;
    const extractedData = {
      likedThreadsCount: likedCount,
      successMessage: `Successfully liked ${likedCount} visible threads`
    };

    return {
      success: true,
      action: "Clicked like buttons on all visible threads in rapid succession",
      extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    // Take screenshot on error
    await page.screenshot({ path: path.join(screenshotsDir, 'error.png') });
    screenshots.push(path.join(screenshotsDir, 'error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}

