import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('#threads-feed', { timeout: 5000 });

    // Find all visible like buttons using multiple selector strategies
    const likeButtons = await page.$$eval('.thread-action[data-action="like"]', 
      buttons => buttons.filter(btn => 
        btn.offsetParent !== null && 
        btn.getBoundingClientRect().top >= 0
      )
    );

    // Click all visible like buttons in rapid succession
    const clickPromises = likeButtons.map(async (button, index) => {
      await button.click();
      // Take screenshot after each click to document progress
      await page.screenshot({ 
        path: path.join(screenshotsDir, `step_2_like_${index + 1}.png`) 
      });
    });

    // Execute all clicks within 5 seconds
    await Promise.race([
      Promise.all(clickPromises),
      new Promise(resolve => setTimeout(resolve, 5000))
    ]);

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_final.png'));

    // Extract data to verify completion
    const likedThreads = await page.$$eval('.thread-action[data-action="like"].active', 
      buttons => buttons.length
    );

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        totalVisibleThreads: likeButtons.length,
        successfullyLiked: likedThreads
      },
      screenshots,
      error: null
    };
  } catch (error) {
    // Take error screenshot
    await page.screenshot({ 
      path: path.join(screenshotsDir, 'step_error.png') 
    });
    screenshots.push(path.join(screenshotsDir, 'step_error.png'));

    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots,
      error: error.message
    };
  }
}
