import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button using ID selector
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForTimeout(1000); // Wait for settings modal to open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Click Appearance tab using CSS attribute selector
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(1000); // Wait for appearance tab to load
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light using select dropdown
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000); // Wait for theme change to apply
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme change by checking selected option
    const selectedTheme = await page.$eval('#theme-select', select => select.value);
    const isLightTheme = selectedTheme === 'light';

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        selectedTheme: selectedTheme,
        themeChangeSuccessful: isLightTheme
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

