import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('#threads-feed');
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Find all visible like buttons
    const likeButtons = await page.$$(".thread-action[data-action='like']");

    // Click all like buttons in rapid succession
    const clickPromises = likeButtons.map(async (button, index) => {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    });

    // Execute all clicks within 5 seconds
    await Promise.race([
      Promise.all(clickPromises),
      new Promise(resolve => setTimeout(resolve, 5000))
    ]);

    // Take screenshot after clicking
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    // Extract thread data for verification
    const threadsData = await page.$$eval('.thread-item', threads => 
      threads.map(thread => ({
        content: thread.querySelector('.thread-content')?.textContent?.trim() || '',
        likes: thread.querySelector('.like-count')?.textContent || '0'
      }))
    );

    return {
      success: true,
      action: "Clicked like buttons on all visible threads",
      extractedData: {
        totalThreads: threadsData.length,
        threads: threadsData
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

