import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load and ensure home view is active
    await page.waitForSelector('#home-view.active');
    await page.waitForSelector('#threads-feed');

    // Find all visible like buttons using multiple selector strategies
    const likeButtons = await page.$$eval(
      '.thread-action[data-action="like"], [data-action="like"], .like-button, button:has(svg.fa-heart)',
      buttons => buttons.filter(btn => btn.offsetParent !== null)
    );

    // Click all visible like buttons rapidly
    for (const button of likeButtons) {
      await button.click();
      await page.waitForTimeout(100); // Small delay between clicks
    }

    // Take screenshot after clicking all like buttons
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_after_likes.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_after_likes.png'));

    // Verify action by checking if buttons show active state
    const likedCount = await page.$$eval(
      '.thread-action[data-action="like"].active, [data-action="like"].active',
      buttons => buttons.length
    );

    return {
      success: true,
      action: `Clicked like buttons on ${likeButtons.length} visible threads`,
      extractedData: {
        totalThreads: likeButtons.length,
        successfullyLiked: likedCount
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
