import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button
    await page.waitForSelector('#settings-btn', { state: 'visible' });
    await page.click('#settings-btn');
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Wait for and click Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]', { state: 'visible' });
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(500);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Select Light theme from dropdown
    await page.waitForSelector('#theme-select', { state: 'visible' });
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000);
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme change by checking body class
    const hasLightTheme = await page.$eval('body', el => el.classList.contains('light-theme'));
    const currentTheme = await page.$eval('#theme-select', select => select.value);

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        themeApplied: hasLightTheme,
        themeValue: currentTheme
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

