import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Switch to search view
    await page.waitForSelector('[data-view="search"]');
    await page.click('[data-view="search"]');
    await page.waitForSelector('#search-view.active');

    // Wait for search input to be visible
    await page.waitForSelector('#search-input');
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_search_view.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_search_view.png'));

    // Generate 20 different search queries
    const searchQueries = [
      "technology", "programming", "design", "art", "science",
      "music", "sports", "travel", "food", "health",
      "fitness", "business", "education", "news", "politics",
      "entertainment", "gaming", "books", "movies", "nature"
    ];

    // Perform 20 rapid search queries
    for (let i = 0; i < searchQueries.length; i++) {
      const query = searchQueries[i];

      // Clear previous search and enter new query
      await page.fill('#search-input', '');
      await page.type('#search-input', query, { delay: 10 });

      // Take screenshot every 5 searches
      if ((i + 1) % 5 === 0) {
        await page.screenshot({ path: path.join(screenshotsDir, `step_3_search_${i + 1}.png`) });
        screenshots.push(path.join(screenshotsDir, `step_3_search_${i + 1}.png`));
      }

      // Small delay between searches
      await page.waitForTimeout(50);
    }

    // Final screenshot after all searches
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_final.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_final.png'));

    return {
      success: true,
      action: "Performed 20 different search queries in rapid succession to stress the search system",
      extractedData: {
        queries: searchQueries,
        count: searchQueries.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

