import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads to load
    await page.waitForSelector('.thread-item', { timeout: 10000 });

    // Extract thread data
    const threadsData = await page.$$eval('.thread-item', threads => {
      return threads.map(thread => {
        const text = thread.querySelector('.thread-text')?.textContent || '';
        const time = thread.querySelector('.thread-time')?.textContent || '';
        const actions = Array.from(thread.querySelectorAll('.thread-action span'))
          .map(span => span.textContent)
          .filter(text => text.trim());

        return {
          text: text.trim(),
          time: time.trim(),
          actions: actions
        };
      });
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the current feed",
      extractedData: {
        threads: threadsData,
        totalThreads: threadsData.length
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract thread content and metadata",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

