import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Wait for threads feed to load
    await page.waitForSelector('#threads-feed', { timeout: 10000 });

    // Wait for individual thread items to be present
    await page.waitForSelector('.thread-item', { timeout: 5000 });

    // Extract all thread content and metadata
    const extractedData = await page.evaluate(() => {
      const threads = [];
      const threadElements = document.querySelectorAll('.thread-item');

      threadElements.forEach((thread) => {
        const textElement = thread.querySelector('.thread-text');
        const timeElement = thread.querySelector('.thread-time');
        const actionElements = thread.querySelectorAll('.thread-action span');

        const actions = Array.from(actionElements).map(el => el.textContent.trim());

        threads.push({
          text: textElement ? textElement.textContent.trim() : '',
          time: timeElement ? timeElement.textContent.trim() : '',
          actions: actions
        });
      });

      return threads;
    });

    // Take screenshot after extraction
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_threads_extracted.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_threads_extracted.png'));

    return {
      success: true,
      action: "Extracted all thread text content and metadata from the feed",
      extractedData: extractedData,
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to extract thread content and metadata",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}
