import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button
    await page.waitForSelector('#settings-btn');
    await page.click('#settings-btn');
    await page.waitForTimeout(1000); // Wait for settings modal to open
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_opened.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_opened.png'));

    // Click Appearance tab
    await page.waitForSelector('.tab-btn[data-tab="appearance"]');
    await page.click('.tab-btn[data-tab="appearance"]');
    await page.waitForTimeout(1000); // Wait for appearance tab to load
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light
    await page.waitForSelector('#theme-select');
    await page.selectOption('#theme-select', 'light');
    await page.waitForTimeout(1000); // Wait for theme change to apply
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    // Verify theme change by checking body class
    const bodyClass = await page.$eval('body', el => el.className);
    const themeValue = await page.$eval('#theme-select', select => select.value);

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        bodyClass,
        themeValue,
        themeChanged: themeValue === 'light'
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

import path from 'path';

export default async function(page, fileUrl, screenshotsDir) {
  try {
    // Navigate to the HTML file
    await page.goto(fileUrl);
    await page.waitForLoadState('networkidle');

    // Take initial screenshot
    const screenshots = [];
    await page.screenshot({ path: path.join(screenshotsDir, 'step_1_initial.png') });
    screenshots.push(path.join(screenshotsDir, 'step_1_initial.png'));

    // Click settings button using different selector strategies
    const settingsBtn = await page.waitForSelector('#settings-btn', { state: 'visible' });
    await settingsBtn.click();
    await page.waitForTimeout(1000); // Wait for modal animation

    // Wait for settings modal to appear and take screenshot
    await page.waitForSelector('.modal-content', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_2_settings_open.png') });
    screenshots.push(path.join(screenshotsDir, 'step_2_settings_open.png'));

    // Click Appearance tab using alternative selector
    const appearanceTab = await page.waitForSelector('[data-tab="appearance"]', { state: 'visible' });
    await appearanceTab.click();
    await page.waitForTimeout(500); // Wait for tab switch

    // Wait for theme selector to be visible and take screenshot
    await page.waitForSelector('#theme-select', { state: 'visible' });
    await page.screenshot({ path: path.join(screenshotsDir, 'step_3_appearance_tab.png') });
    screenshots.push(path.join(screenshotsDir, 'step_3_appearance_tab.png'));

    // Change theme to Light using different interaction method
    const themeSelect = await page.$('#theme-select');
    await themeSelect.selectOption('light');
    await page.waitForTimeout(500); // Wait for theme change

    // Verify theme change by checking selected value
    const selectedTheme = await page.$eval('#theme-select', select => select.value);
    if (selectedTheme !== 'light') {
      throw new Error('Theme was not changed to light');
    }

    // Take final screenshot
    await page.screenshot({ path: path.join(screenshotsDir, 'step_4_theme_changed.png') });
    screenshots.push(path.join(screenshotsDir, 'step_4_theme_changed.png'));

    return {
      success: true,
      action: "Opened settings, navigated to Appearance tab, and changed theme to Light",
      extractedData: {
        selectedTheme: selectedTheme,
        themeOptions: ['dark', 'light', 'auto']
      },
      screenshots,
      error: null
    };
  } catch (error) {
    return {
      success: false,
      action: "Failed to complete task",
      extractedData: {},
      screenshots: [],
      error: error.message
    };
  }
}

